import React from 'react';
import {connect} from 'react-redux';
import { menuChange, getCourse } from '../../../actions';

import GoogleAuth from '../../GoogleAuth';
import FormCourse from './FormCourse';

import './CreateDoc.scss';

class EditDoc extends React.Component {
    componentDidMount() {
        this.props.menuChange('courses');
        this.props.getCourse(this.props.match.params.id);
    }

    onSubmit = async (formValues) => {
        console.log(formValues);
    }

    render() {
        console.log(this.props.courseInfo, "courseInfo");
        return (
            <div className="CreateDoc">
                <GoogleAuth />
                <h1>Edit  :)</h1>
                <div className="formCreateDoc">
                    <FormCourse isSignedIn={this.props.isSignedIn} onSubmit={this.onSubmit} editValues={this.props.userId} />
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state, ownProps) => {
    if (state.cours[`course_${ownProps.match.params.id}`]) return {  isSignedIn : state.auth.isSignedIn, userId: state.auth.userId, courseInfo: state.cours[`course_${ownProps.match.params.id}`][0][0], file: state.cours[`course_${ownProps.match.params.id}`][1] };
    return {isSignedIn : state.auth.isSignedIn, userId: state.auth.userId};
}

export default connect(mapStateToProps, {menuChange, getCourse})(EditDoc);
