import React from 'react';
import { connect } from 'react-redux';

import { menuChange, getCourses } from '../../../actions';

import eye_icon from '../../eye_logo.png';
import like_icon from '../../like.jpeg';

import './Search.scss';

class Search extends React.Component {
    componentDidMount() {
        this.props.menuChange('search');
        this.props.getCourses();
    }

    renderMenu = () => {
        return (
            <div className="menu_search_container">
                <div className="menu_search_element">
                    <span className="element element_right element_left">
                        Histoire
                    </span>
                </div>
                <div className="menu_search_element">
                    <div>
                        <span className="element element_left">This Month</span>
                        <span className="element">Last 6 months</span>
                        <span className="element">This Year</span>
                        <span className="element element_right">Last 3 years</span>
                    </div>
                    <div>
                        <span className="element element_left">PDF</span>
                        <span className="element">Text</span>
                        <span className="element element_right active">Image</span>
                    </div>
                    <div>
                        <span className="element element_right element_left">Pinned</span>
                    </div>
                    <div>
                        <span className="element element_left">Most Views</span>
                        <span className="element">Most Likes</span>
                        <span className="element element_right">Most Pins</span>
                    </div>
                </div>
            </div>
        );
    }

    renderList = () => {
        return this.props.courses.map((cours) => {
            return (
                <div key={cours.id} className="stats_profile_courseItem" style={{backgroundColor: 'rgba(0,0,0,0)'}}>
                    <div className="stats_profile_title">
                        <div>
                            <h3>{cours.matiere}</h3>
                            <img src={cours.image} height="80px" />
                        </div>
                        <div className="stats_profile_courseItem_left search">
                            <h3 className="search_title">{cours.titleDoc}</h3>
                            <p className="search_description">{cours.description}</p>
                            <div className="search_belowDescription">
                                <span>{cours.creator}</span>
                                <div className="stats_profile_courseItem_tags">{cours.tags.split("#").slice(1, 4).map((tag)=><span key={tag}>{'#'+tag}</span>)}</div>
                            </div>

                        </div>
                    </div>
                    <div className="stats_profile_courseItem_right search">
                        <div>{cours.viewed}&nbsp;&nbsp;<img src={eye_icon} width="25px" height="15px"/></div>
                        <div>{cours.liked}&nbsp;&nbsp;<img src={like_icon} width="20px" height="20px"/></div>
                    </div>
                </div>
            );
        })
    }

    render() {
        if (!this.props.courses) return null;

        return (
            <div className="Search">
                {this.renderMenu()}
                <div className="search_list">
                    {this.renderList()}
                </div>
            </div>
        );
    }
};

const mapStateToProps = (state) => {
    return {  isSignedIn : state.auth.isSignedIn, courses:state.cours.tt_cours };
}

export default connect(mapStateToProps, {menuChange, getCourses})(Search);
