export default (state={}, action) => {
    switch (action.type) {
        case 'GET_COURSES': return {  ...state, tt_cours: action.payload  };
        case 'GET_COURSE': 
            const info = action.payload[0];
            return {...state, [`course_${info.id}`]: info};
        default:
            return {...state}
    }
}
