import React from 'react';
import { connect } from 'react-redux';
import Dropdown from './Dropdown';

import { getCourses } from '../../../actions';

class QueryButton extends React.Component {
    state = {elementList: []}

    onElementClick = (elem) => {
        if (this.state.elementList.includes(elem)) {
            const newArray = this.state.elementList.filter((item) => item !== elem);
            this.setState({elementList: newArray});
        } else {
            const newArray = [...this.state.elementList, elem];
            this.setState({elementList: newArray});
        }
    }

    renderClassName = (className, name) => {
        return this.state.elementList.includes(name) ? className + " active": className;
    }

    render() {
        console.log(this.state.elementList);
        return (
            <div className="menu_search_container">
                <div className="menu_search_element">
                    <span>
                        <Dropdown info={[0, 1, 2]}>
                            <div onClick={() => this.onElementClick("histoire")}>
                                Histoire
                            </div>
                            <div onClick={() => this.onElementClick("math")}>
                                Math
                            </div>
                            <div onClick={() => this.onElementClick("geographie")}>
                                Géographie
                            </div>
                        </Dropdown>
                    </span>
                </div>
                <div className="menu_search_element">
                    <div className={`${this.props.notActive ? "notActive" : ""}`}>
                        <span className={this.renderClassName("element element_left", "month_2")} onClick={() => this.onElementClick("month_2")}>This Month</span>
                        <span className={this.renderClassName("element", "sixMonth_2")} onClick={() => this.onElementClick("sixMonth_2")}>Last 6 months</span>
                        <span className={this.renderClassName("element", "year_2")} onClick={() => this.onElementClick("year_2")}>This Year</span>
                        <span className={this.renderClassName("element element_right", "threeYear_2")} onClick={() => this.onElementClick("threeYear_2")}>Last 3 years</span>
                    </div>
                    <div className={`${this.props.notActive ? "notActive" : ""}`}>
                        <span className={this.renderClassName("element element_left", "pdf")} onClick={() => this.onElementClick("pdf")}>PDF</span>
                        <span className={this.renderClassName("element", "text")} onClick={() => this.onElementClick("text")}>Text</span>
                        <span className={this.renderClassName("element element_right", "image")} onClick={() => this.onElementClick("image")}>Image</span>
                    </div>
                    <div className={`${this.props.notActive ? "notActive" : ""}`}>
                        <span className={this.renderClassName("element element_left element_right", "pinned")} onClick={() => this.onElementClick("pinned")}>Pinned</span>
                    </div>
                    <div>
                        <span className={this.renderClassName("element element_left", "mostViews")} onClick={() => this.onElementClick("mostViews", this.state.lastQuerySubmit)}>Most Views</span>
                        <span className={this.renderClassName("element", "mostLikes")} onClick={() => this.onElementClick("mostLikes", this.state.lastQuerySubmit)}>Most Likes</span>
                        <span className={this.renderClassName("element element_right", "mostPins")} onClick={() => this.onElementClick("mostPins", this.state.lastQuerySubmit)}>Most Pins</span>
                    </div>
                </div>
            </div>
        );
    }
}

export default connect(null, {getCourses})(QueryButton);
