import React from 'react';
import _ from 'lodash';
import { connect } from 'react-redux';

import { menuChange, getCourses } from '../../../actions';

import eye_icon from '../../eye_logo.png';
import like_icon from '../../like.jpeg';
import search_icon from '../../Header/header_image/search.png';

import './Search.scss';

class Search extends React.Component {
    state = { elementClicked: null, lastQuerySubmit: "" }
    constructor(props) {
        super(props);
        this.myRef = React.createRef();
    }

    componentDidMount() {
        this.props.menuChange('search');
        this.props.getCourses(null);
    }
    componentDidUpdate() {
        this.myRef.current.addEventListener('keypress', (e) => this.onSearchButtonClick(e));
    }
    componentWillUnmount() {
        this.myRef.current.removeEventListener('keypress', (e) => this.onSearchButtonClick(e));
    }
    onSearchButtonClick = (e) => {
        if (e.key === 'Enter') {
            this.props.getCourses("search", this.myRef.current.value);
            this.setState({lastQuerySubmit: this.myRef.current.value});
            this.myRef.current.blur();
        }
    }

    onElementClick = (elem, more="") => {
        if (this.state.elementClicked === elem) {
            this.setState({ elementClicked: null });
            this.props.getCourses(" ");
        } else {
            this.setState({ elementClicked: elem });
            this.props.getCourses(elem, more);
        }
    }

    renderClassName = (className, name) => {
        return this.state.elementClicked === name ? className + " active": className;
    }

    renderMenu = () => {
        return (
            <div className="menu_search_container">
                <div className="menu_search_element">
                    <span className="element element_right element_left" onClick={() => this.onElementClick("hist_1")}>
                        Histoire
                    </span>
                </div>
                <div className="menu_search_element">
                    <div>
                        <span className={this.renderClassName("element element_left", "month_2")} onClick={() => this.onElementClick("month_2")}>This Month</span>
                        <span className={this.renderClassName("element", "sixMonth_2")} onClick={() => this.onElementClick("sixMonth_2")}>Last 6 months</span>
                        <span className={this.renderClassName("element", "year_2")} onClick={() => this.onElementClick("year_2")}>This Year</span>
                        <span className={this.renderClassName("element element_right", "threeYear_2")} onClick={() => this.onElementClick("threeYear_2")}>Last 3 years</span>
                    </div>
                    <div>
                        <span className={this.renderClassName("element element_left", "pdf")} onClick={() => this.onElementClick("pdf")}>PDF</span>
                        <span className={this.renderClassName("element", "text")} onClick={() => this.onElementClick("text")}>Text</span>
                        <span className={this.renderClassName("element element_right", "image")} onClick={() => this.onElementClick("image")}>Image</span>
                    </div>
                    <div>
                        <span className={this.renderClassName("element element_left element_right", "pinned")} onClick={() => this.onElementClick("pinned")}>Pinned</span>
                    </div>
                    <div>
                        <span className={this.renderClassName("element element_left", "mostViews")} onClick={() => this.onElementClick("mostViews", this.state.lastQuerySubmit)}>Most Views</span>
                        <span className={this.renderClassName("element", "mostLikes")} onClick={() => this.onElementClick("mostLikes", this.state.lastQuerySubmit)}>Most Likes</span>
                        <span className={this.renderClassName("element element_right", "mostPins")} onClick={() => this.onElementClick("mostPins", this.state.lastQuerySubmit)}>Most Pins</span>
                    </div>
                </div>
            </div>
        );
    }

    renderList = () => {
        return this.props.courses.map((cours) => {
            return (
                <div key={cours.id} className="stats_profile_courseItem" style={{backgroundColor: 'rgba(0,0,0,0)'}}>
                    <div className="stats_profile_title">
                        <div>
                            <h3>{cours.matiere}</h3>
                            <img src={cours.image} height="80px" />
                        </div>
                        <div className="stats_profile_courseItem_left search">
                            <h3 className="search_title">{cours.titleDoc}</h3>
                            <p className="search_description">{cours.description}</p>
                            <div className="search_belowDescription">
                                <span>{cours.creator}</span>
                                <div className="stats_profile_courseItem_tags">{cours.tags.split("#").slice(1, 4).map((tag)=><span key={tag}>{'#'+tag}</span>)}</div>
                            </div>

                        </div>
                    </div>
                    <div className="stats_profile_courseItem_right search">
                        <div>{cours.viewed}&nbsp;&nbsp;<img src={eye_icon} width="25px" height="15px"/></div>
                        <div>{cours.liked}&nbsp;&nbsp;<img src={like_icon} width="20px" height="20px"/></div>
                    </div>
                </div>
            );
        })
    }

    render() {
        if (!this.props.courses) return null;

        return (
            <div className="Search">
                <div className="search_bar_container">
                    <div className="search_bar">
                        <div>
                            <input ref={this.myRef} type="search" placeholder="Search..."/>
                            <button onClick={()=>this.props.getCourses("search", this.myRef.current.value)}><img src={search_icon} height="20px"/></button>
                        </div>
                    </div>
                </div>
                {this.renderMenu()}
                <div className="search_list">
                    {this.renderList()}
                </div>
            </div>
        );
    }
};

const mapStateToProps = (state) => {
    return {  isSignedIn : state.auth.isSignedIn, courses:state.cours.tt_cours };
}

export default connect(mapStateToProps, {menuChange, getCourses})(Search);
