import React from 'react';
import { connect } from 'react-redux';

import {getCourse} from '../../../actions';
import { BodyContext } from '../Body';

import './DocGet.scss';

console.log(BodyContext);

class DocGet extends React.Component {
    componentDidMount() {
        this.props.getCourse(this.props.match.params.id);
    }
    componentDidUpdate() {
        console.log(document.getElementById('#body'))
    }

    render() {
        if (!this.props.courseInfo) return null;

        return (
            <div>
                <div className="docget_header">
                    <div>
                        <div className="banner">
                            <img src={this.props.courseInfo.image} />
                        </div>

                        <h2>{this.props.courseInfo.titleDoc}</h2>
                        <p>{this.props.courseInfo.description}</p>
                    </div>
                    <div>
                    </div>
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state, ownProps) => {
    return {  courseInfo: state.cours[`course_${ownProps.match.params.id}`] };
};

export default connect(mapStateToProps, {getCourse})(DocGet);
