import React from 'react';
import { connect } from 'react-redux';

import { menuChange, getCourses } from '../../../actions';

class Search extends React.Component {
    componentDidMount() {
        this.props.menuChange('search');
        this.props.getCourses();
    }

    renderList = () => {
        return this.props.courses.map((cours) => {
            return (
                <div key={course.id} className="stats_profile_courseItem">
                    <div>
                        <img src={course.image} height="60px" />
                        <div className="stats_profile_courseItem_left">
                            <h3>{course.titleDoc}</h3>
                            <div className="stats_profile_courseItem_tags">{course.tags.split("#").slice(1, 4).map((tag)=><p key={tag}>{'#'+tag}</p>)}</div>
                        </div>
                    </div>
                    <div className="stats_profile_courseItem_right">
                        <div><img src={eye_icon} width="14px" height="7px"/>&nbsp;&nbsp;{course.viewed}</div>
                        <div><img src={like_icon} width="10px" height="10px"/>&nbsp;&nbsp;{course.liked}</div>
                    </div>
                </div>
            );
        })
    }

    render() {
        if (!this.props.courses) return null;

        return (
            <div className="Search">
                <div>
                    {this.renderList()}
                </div>
            </div>
        );
    }
};

const mapStateToProps = (state) => {
    return {  isSignedIn : state.auth.isSignedIn, courses:state.cours.tt_cours };
}

export default connect(mapStateToProps, {menuChange, getCourses})(Search);
