import React from 'react';
import { connect } from 'react-redux';

import Logo from '../../Header/Logo';
import GoogleAuth from '../../GoogleAuth';
import JoinIn from './JoinIn';
import { menuChange } from '../../../actions';

import './Home.scss';

class Home extends React.Component {
    state = { firstShowed: true, showJoinIn: true };

    componentDidMount() {
        this.props.menuChange('home');
    }

    onDismiss = (bool) => {
        this.setState({ showJoinIn: bool })
    }

    displayJoinIn = () => {
        console.log(this.state.showJoinIn, this.state.firstShowed, this.state.showJoinIn && this.state.firstShowed)
        if (this.state.showJoinIn && this.state.firstShowed) {
            this.setState({firstShowed:false});
            return <JoinIn onDismiss={this.onDismiss} />;
        };

        return <div></div>;
    }

    render() {
        return (
            <div className="home">
                <div className="join">
                    <GoogleAuth text="Sign In" buttonColor="red" displayButton />
                    <GoogleAuth text="Log In" buttonColor="blue" displayButton />
                    {/*<button onClick={() => this.onDismiss(true)}>Join Us</button>*/}
                    {this.displayJoinIn()}
                </div>
            </div>
        );
    }
}

export default connect(null, {menuChange})(Home);
