import _ from 'lodash';

const INITIAL_STATE = {
    isSignedIn: null
};

export default (state=INITIAL_STATE, action) => {
    switch (action.type) {
        case SIGN_IN:
            return {...state, isSignedIn:true, userId: action.payload.id, userInfo: action.payload.info};
        case SIGN_OUT:
            return _.omit({...state, isSignedIn: false}, ['userId', ]);
        case 'CREATE_USER':
            return {...state, basicInfo:action.payload }
        default:
            return state;
    }
};
