import React from 'react';
import {connect} from 'react-redux';
import { Field, reduxForm } from 'redux-form';

class FormCourse extends React.Component {
    componentDidMount() {

    }

    renderError({ error, touched }) {
        if (touched && error) {
            return (
                <div className="ui error message">
                    <div className="header">{error}</div>
                    {this.checkErrorUpload}
                </div>
            );
        }
    }

    renderInput = ({ input, label, meta }) => {
        const className = `form_item field ${meta.error && meta.touched ? 'error' : ''}`;

        return (
            <div className={className}>
                <label>{label}</label>
                <input {...input} autoComplete="off" />
                {this.renderError(meta)}
            </div>
        );
    }

    render() {
        return (
            <form onSubmit={this.props.handleSubmit(this.onSubmit)} className="ui form error">
                <Field name="title" component={this.renderInput} label={<p className="label">Enter Title</p>} />
                <Field name="description" component={this.renderInput} label={<p className="label">Enter Description</p>} />
                <button className="ui button primary">Submit</button>
            </form>
        );
    }
}

const validate = (formValues) => {
    const errors = {};
    return errors;
};

export default reduxForm({
    form: 'DocumentForm',
    validate
})(FormCourse);
