export default (state={}, action) => {
    const {  payload  } = action;
    switch (action.type) {
        case 'GET_COURSES': return {  ...state, tt_cours: payload  };
        case 'GET_COURSE': return {...state, [`course_${payload.id}`]: payload};
        default:
            return {...state}
    }
}
