import React from 'react';
import {connect} from 'react-redux';
import { Field, reduxForm } from 'redux-form';

class FormCourse extends React.Component {
    componentDidMount() {

    }

    renderError({ error, touched }) {
        if (touched && error) {
            return (
                <div className="ui error message">
                    <div className="header">{error}</div>
                    {this.checkErrorUpload}
                </div>
            );
        }
    }

    onSubmit = (formValues) => {
        this.props.onSubmit({...formValues});
    }

    renderInput = ({ input, label, meta }, textarea=false) => {
        const className = `form_item field ${meta.error && meta.touched ? 'error' : ''}`;

        return (
            <div className={className}>
                <label>{label}</label>
                {textarea ? <input {...input} autoComplete="off" /> : <textarea {...input} />}
                {this.renderError(meta)}
            </div>
        );
    }

    render() {
        return (
            <form onSubmit={this.props.handleSubmit(this.onSubmit)} className="ui form error">
                <Field name="titleDoc" component={this.renderInput} label={<p className="label">Title</p>} />
                <Field name="description" component={this.renderInput} props={{putTextarea: true}} label={<p className="label">Description</p>} />
                <Field name="tags" component={this.renderInput} label={<p className="label">Tags</p>} />

                <p className="label" htmlFor="matiere">Matière</p>
                <Field name="matiere" component="select">
                    <option value="anglais">Anglais</option>
                    <option value="français">Français</option>
                    <option value="pc">Physique-Chimie</option>
                    <option value="math">Mathématiques</option>
                    <option value="geographie">Géographie</option>
                    <option value="svt">SVT</option>
                    <option value="histoire">Histoire</option>
                </Field>
                <button className="ui button primary">Submit</button>
            </form>
        );
    }
}

const validate = (formValues) => {
    const errors = {};
    return errors;
};

export default reduxForm({
    form: 'DocumentForm',
    validate
})(FormCourse);
