import React from 'react';
import {connect} from 'react-redux';
import { Field, reduxForm } from 'redux-form';

import './FormCourse.scss';

class FormCourse extends React.Component {
    componentDidMount() {

    }

    renderError({ error, touched }) {
        if (touched && error) {
            return (
                <div className="">
                    <div className="">{error}</div>
                    {this.checkErrorUpload}
                </div>
            );
        }
    }

    onSubmit = (formValues) => {
        this.props.onSubmit({...formValues});
    }

    renderInput = ({ input, label, meta }) => {
        const className = `form_item field ${meta.error && meta.touched ? 'error' : ''}`;

        return (
            <div className={className}>
                <label>{label}</label>
                <input {...input} autoComplete="off" /> 
                {this.renderError(meta)}
            </div>
        );
    }
    renderTextArea = ({ input, label, meta }) => {
        return (
            <textarea {...input} /> 
        );
    }

    render() {
        return (
            <form onSubmit={this.props.handleSubmit(this.onSubmit)} className="formCourse">
                <div className="itemCreate">
                    <label className="label" htmlFor="title">Title </label><Field name="title" component={this.renderInput}/>
                </div>

                <div className="itemCreate">
                    <label className="label" htmlFor="description">Description </label><Field name="description" component={this.renderTextArea}/>
                </div>

                <div className="itemCreate">
                    <label className="label" htmlFor="tags">Tags </label><Field name="tags" component={this.renderInput}/>
                </div>

                <div className="itemCreate">
                    <label className="label" htmlFor="matiere">Matière</label>
                    <Field name="matiere" className="form_item field" component="select">
                        <option></option>
                        <option value="anglais">Anglais</option>
                        <option value="français">Français</option>
                        <option value="pc">Physique-Chimie</option>
                        <option value="math">Mathématiques</option>
                        <option value="geographie">Géographie</option>
                        <option value="svt">SVT</option>
                        <option value="histoire">Histoire</option>
                    </Field>
                </div>

                <button className="ui button primary">Submit</button>
            </form>
        );
    }
}

const validate = (formValues) => {
    const errors = {};
    return errors;
};

export default reduxForm({
    form: 'DocumentForm',
    validate
})(FormCourse);
