import React from 'react';
import { connect } from 'react-redux';
import { menuChange, getForum, getPostsFromForum } from '../../../actions';
import LikeImage from '../../heart.png';
import LikeFullImage from '../../heartFull.png';
import ForumGetElement from './ForumGetElement';

import './ForumGet.scss';


class ForumGet extends React.Component {
    state = {liked: null}

    componentDidMount() {
        this.props.menuChange('forums');
        this.props.getForum(this.props.match.params.id);
        this.props.getPostsFromForum(this.props.match.params.id);
        this.codeCouleur = {
            'pc': '#ff780a', //orange
            'math': '#e61c1c', //red
            'geographie': '#9b1ce6', //purple
            'anglais': '#163bf2', //blue
            'svt': '#206b0b', //green
            'français': '#eded24', //yellow
            'histoire': '#db1dc8' //pink
        };
        this.translateMatiere = {
            'pc': 'Physique',
            'math': 'Mathématiques',
            'geographie': 'Géographie',
            'anglais': 'Anglais',
            'svt': 'Science de la vie et de la Terre',
            'français': 'Français',
            'histoire': 'Histoire'
        };
    }

    renderList() {
        return this.props.forumMessages.map((mes) => {
            return (
                <ForumGetElement key={`${mes.id}_message`} mes={mes} />
            );
        });
    }

    render() {
        if (!this.props.forumInfo || !this.props.forumMessages) return null 

        const { forumInfo } = this.props;
        const date = new Date(forumInfo.dateCreated);

        return (
            <div>
                <div className="forumGetHeader">
                    <div className="forumGetTitle">
                        <h2>{forumInfo.titleForum}</h2>
                        <div className="matiereTag"><div className="forumGetPoint" style={{'backgroundColor': this.codeCouleur[forumInfo.matiere]}}></div> {this.translateMatiere[forumInfo.matiere]}</div>
                    </div>
                    <div className="forumGetDate" style={{"fontSize": "20px"}}>{date.toLocaleString("fr",{minute: "2-digit", hour: "2-digit", day: 'numeric', month: 'long', year: 'numeric'})}</div>
                </div>
                <div className="forumGetPostsList">
                    {this.renderList()}
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state, ownProps) => {
    return {  forumInfo: state.forum[`forum_${ownProps.match.params.id}`], forumMessages: state.forum[`forum_${ownProps.match.params.id}_posts`] };
};

export default connect(mapStateToProps, { menuChange, getForum, getPostsFromForum })(ForumGet);
