import server from '../apis/server';

export const menuChange = (to) => {
    return { type: 'ON_MENU_CHANGE', payload: to};
};

export const signIn = (userInfo) => async (dispatch) => {
    // get info from data base
    const response = await server.get(`/getUserInfo/${userInfo.getId()}`);
    dispatch({ type: 'SIGN_IN', payload: {id: userInfo.getId(), info: response.data[0]}});
};
export const signOut = () => {
    return { type: 'SIGN_OUT' }
};

export const getCoursesFrom = (userId) => async (dispatch) => {
    const response = await server.get(`/getCourseUserId/${userId}`);
    dispatch({ type:'GET_COURSE_FROM_ID', payload: response.data });
};

export const getCourses = (query, more="", userId="") => async (dispatch) => {
    let querySent = 'a';
    switch (query) {
        case 'month_2': querySent = 'month_2'; break
        case "sixMonth_2": querySent ='6month_2'; break
        case "year_2": querySent ='year_2'; break
        case "threeYear_2": querySent ='3years_2'; break
        case "pdf": querySent = 'pdf'; break
		case "text": querySent = 'text'; break
		case "image": querySent = 'image'; break
        case "mostViews": querySent = `mostViews&search=${more}`; break
        case "mostLikes": querySent = `mostLikes&search=${more}`; break
        case "mostPins": querySent = `mostPins&search=${more}`; break
        case "search": querySent = `searchQuery&search=${more}`;
    }

    const response = await server.get(`/getCourses?filter=${querySent}`);

    dispatch({ type:'GET_COURSES', payload: response.data });
};
