import React from 'react';
import { connect } from 'react-redux';

import { menuChange } from '../../../actions';

import './Statistics.scss';

class Statistics extends React.Component {
    state = { activeButton:1 }
    componentDidMount() {
        this.props.menuChange('statistics');

        switch (this.props.match.params.where) {
            case "profile": this.setState({activeButton:1});
            case "courses": this.setState({activeButton:2});
            case "forum": this.setState({activeButton:3});
            case "hall_of_frame": this.setState({activeButton:4});
            default: console.log("merde")
        }
    }

    renderProfile() {
        const { userInfo } = this.props;
        return (
            <div className="stats_profile">
                <div>
                    <img className="stats_profileImag" src={userInfo.profileImage} width="200px" />
                    <div className="stats_infosProfile">
                        <h4>{userInfo.firstName + " " + userInfo.lastName}</h4>
                        <div className="stats_infos_container">
                            <p><strong>Classe: </strong>{userInfo.classe}</p>
                            <p><strong>Ã‰tude: </strong>{userInfo.lieuEtude}</p>
                            <p><strong>Connection: </strong>{userInfo.lieuEtude}</p>
                            <p><strong>Emplacement: </strong>{userInfo.location}</p>
                        </div>
                    </div>
                </div>
                <div>
                    <h1>{userInfo.userName}</h1>
                    <p>{userInfo.biography}</p>
                </div>
            </div>
        );
    }

    renderStats() {
        if (!this.props.userInfo) return <div>Loading..</div>;

        switch (this.state.activeButton) {
            case 1:
                return this.renderProfile();
            case 2:
                return <div>2</div>
            case 3:
                return <div>3</div>
            case 4:
                return <div>4</div>
        }
    }

    render() {
        console.log(this.props.match);
        return (
            <div className="Statistics">
                {/*this.props.userInfo.id */}
                <div className="stats_button_cont">
                    <button className={this.state.activeButton===1 ? 'active' : ''} onClick={()=>this.setState({activeButton:1})}>Profile</button>
                    <button className={this.state.activeButton===2 ? 'active' : ''} onClick={()=>this.setState({activeButton:2})}>Courses</button>
                    <button className={this.state.activeButton===3 ? 'active' : ''} onClick={()=>this.setState({activeButton:3})}>Forum</button>
                    <button className={this.state.activeButton===4 ? 'active' : ''} onClick={()=>this.setState({activeButton:4})}>Hall of Frame</button>
                </div>
                <div className="statsShow">
                    {this.renderStats()}
                </div>
            </div>
        );
    }
};

const mapStateToProps = (state) => {
    return {  isSignedIn : state.auth.isSignedIn, userInfo: state.auth.userInfo };
}

export default connect(mapStateToProps, {menuChange})(Statistics);
