import React from 'react';

import RightArrow from '../../rightArrow.png';

import './Dropdown.scss';

class Dropdown extends React.Component {
    state = {dropdownClick: false, elementChosen: null}

    componentDidMount() {
        this.setState({elementChosen: this.props.info[0]});
    }

    render() {
        return (
            <div>
                <div className="dropdown">
                    <div>
                        {this.props.children[this.state.elementChosen]}
                    </div>
                    <img onClick={()=>this.setState({dropdownClick: !this.state.dropdownClick})} className={this.state.dropdownClick ? "clicked" : ""} src={RightArrow} width="10px" height="10px" />
                </div>
                {this.state.dropdownClick ? this.props.info.filter((elem) => elem !== this.state.elementChosen) : ""}
            </div>
        );
    }
}

export default Dropdown;
