import React from 'react';
import { connect } from 'react-redux';

import { menuChange } from '../../../actions';

import './Statistics.scss';

class Statistics extends React.Component {
    componentDidMount() {
        this.props.menuChange('statistics');
    }

    onStatsButtonChange = (pathing) => {
        console.log("assa", pathing);
        this.props.activeButton = pathing;
    }

    renderProfile() {
        const { userInfo } = this.props;
        return (
            <div className="stats_profile">
                <div>
                    <img className="stats_profileImag" src={userInfo.profileImage} width="200px" />
                    <div className="stats_infosProfile">
                        <h4>{userInfo.firstName + " " + userInfo.lastName}</h4>
                        <div className="stats_infos_container">
                            <p><strong>Classe: </strong>{userInfo.classe}</p>
                            <p><strong>Ã‰tude: </strong>{userInfo.lieuEtude}</p>
                            <p><strong>Connection: </strong>{userInfo.lieuEtude}</p>
                            <p><strong>Emplacement: </strong>{userInfo.location}</p>
                        </div>
                    </div>
                </div>
                <div>
                    <h1>{userInfo.userName}</h1>
                    <p>{userInfo.biography}</p>
                </div>
            </div>
        );
    }

    renderStats() {
        if (!this.props.userInfo) return <div>Loading..</div>;

        switch (this.props.activeButton) {
            case 'profile':
                return this.renderProfile();
            case 'courses':
                return <div>2</div>
            case 'forum':
                return <div>3</div>
            case 'hall_of_frame':
                return <div>4</div>
        }
    }

    render() {
        console.log(this.props.match);
        return (
            <div className="Statistics">
                {/*this.props.userInfo.id */}
                <div className="stats_button_cont">
                    <button className={this.props.activeButton==='profile' ? 'active' : ''} onClick={() => this.onStatsButtonChange('profile')}>Profile</button>
                    <button className={this.props.activeButton==='courses' ? 'active' : ''} onClick={() => this.onStatsButtonChange('courses')}>Courses</button>
                    <button className={this.props.activeButton==='forum' ? 'active' : ''} onClick={() => this.onStatsButtonChange('forum')}>Forum</button>
                    <button className={this.props.activeButton==='hall_of_frame' ? 'active' : ''} onClick={() => this.onStatsButtonChange('hall_of_frame')}>Hall of Frame</button>
                </div>
                <div className="statsShow">
                    {this.renderStats()}
                </div>
            </div>
        );
    }
};

const mapStateToProps = (state) => {
    return {  isSignedIn : state.auth.isSignedIn, userInfo: state.auth.userInfo };
}

export default connect(mapStateToProps, {menuChange})(Statistics);
