import React from 'react';
import { connect } from 'react-redux';
import { menuChange, getForum, getPostsFromForum } from '../../../actions';
import LikeImage from '../../heart.png';
import LikeFullImage from '../../heartFull.png';

import './ForumGet.scss';


class ForumGet extends React.Component {
    state = {liked: null}

    componentDidMount() {

    }

    onLikedClicked = () => {
        if (this.state.liked === null) {
            this.setState({liked:true});
            //this.props.likedContent(this.props.courseInfo.id, 1, this.props.courseInfo.creator);
            this.props.courseInfo.liked++;
        } else {
            this.setState({liked:null});
            //this.props.likedContent(this.props.courseInfo.id, -1, this.props.courseInfo.creator);
            this.props.courseInfo.liked--;
        }
    }

    render() {
        const { forumMessages } = this.props;
        console.log(forumMessages);

        return (
            <div className="forumGetPostsList">
                {
                    forumMessages.map((mes) => {
                        const date = new Date(mes.datePosted);
                        return (
                            <div key={`${mes.id}_message`} className="forumGetMessage">
                                <div>
                                    <div className="forumGetMessageHeader">
                                        <img className="forumGetProfileImage" src={mes.profileImage}/>
                                        <div>{mes.userName}</div>
                                    </div>
                                    <div className="forumGetMessageBody">
                                        <div dangerouslySetInnerHTML={{__html: mes.message}} />
                                        <div>
                                            <img src={this.state.liked ? LikeFullImage : LikeImage} width="15px" height="15px" onClick={() => this.onLikedClicked()}/> {mes.likes}
                                        </div>
                                    </div>
                                </div>

                                <div>
                                    <span>{date.toLocaleString("fr",{day: 'numeric', month: 'long', year: 'numeric'})}</span>
                                </div>
                            </div>
                        );
                    })
                }
            </div>
        );
    }
}

export default connect(mapStateToProps, { getForum, getPostsFromForum })(ForumGet);
