import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';

import { menuChange } from '../../../actions';

import './Statistics.scss';

class Statistics extends React.Component {
    state = { activeButton: 'profile' }

    componentDidMount() {
        this.props.menuChange('statistics');
        console.log(this.props.match.params.where);
        this.setState({ activeButton: this.props.match.params.where });
    }

    renderProfile() {
        const { userInfo } = this.props;
        return (
            <div className="stats_profile">
                <div>
                    <img className="stats_profileImag" src={userInfo.profileImage} width="200px" />
                    <div className="stats_infosProfile">
                        <h4>{userInfo.firstName + " " + userInfo.lastName}</h4>
                        <div className="stats_infos_container">
                            <p><strong>Classe: </strong>{userInfo.classe}</p>
                            <p><strong>Étude: </strong>{userInfo.lieuEtude}</p>
                            <p><strong>Connection: </strong>{userInfo.lieuEtude}</p>
                            <p><strong>Emplacement: </strong>{userInfo.location}</p>
                        </div>
                    </div>
                </div>
                <div>
                    <h1>{userInfo.userName}</h1>
                    <p>{userInfo.biography}</p>
                </div>
            </div>
        );
    }

    renderStats() {
        if (!this.props.userInfo) return <div>Loading..</div>;

        console.log("wd", this.props.activeButton);
        switch (this.props.activeButton) {
            case 'profile':
                return this.renderProfile();
            case 'courses':
                return <div>2</div>
            case 'forum':
                return <div>3</div>
            case 'hall_of_frame':
                return <div>4</div>
        }
    }

    render() {
        return (
            <div className="Statistics">
                <div className="stats_button_cont">
                    <Link to="/statistics/profile"><button className={this.props.activeButton==='profile' ? 'active' : ''}>Profile</button></Link>
                    <Link to="/statistics/courses"><button className={this.props.activeButton==='courses' ? 'active' : ''}>Courses</button></Link>
                    <Link to="/statistics/forum"><button className={this.props.activeButton==='forum' ? 'active' : ''}>Forum</button></Link>
                    <Link to="/statistics/hall_of_frame"><button className={this.props.activeButton==='hall_of_frame' ? 'active' : ''}>Hall of Frame</button></Link>
                </div>
                <div className="statsShow">
                    {this.renderStats()}
                </div>
            </div>
        );
    }
};

const mapStateToProps = (state) => {
    return {  isSignedIn : state.auth.isSignedIn, userInfo: state.auth.userInfo };
}

export default connect(mapStateToProps, {menuChange})(Statistics);
