import React from 'react';
import { connect } from 'react-redux';
import { menuChange, getForum, getPostsFromForum } from '../../../actions';
import LikeImage from '../../heart.png';
import LikeFullImage from '../../heartFull.png';

import './ForumGet.scss';


class ForumGet extends React.Component {
    state = {liked: null}

    componentDidMount() {

    }

    onLikedClicked = () => {
        if (this.state.liked === null) {
            this.setState({liked:true});
            //this.props.likedContent(this.props.courseInfo.id, 1, this.props.courseInfo.creator);
            this.props.courseInfo.liked++;
        } else {
            this.setState({liked:null});
            //this.props.likedContent(this.props.courseInfo.id, -1, this.props.courseInfo.creator);
            this.props.courseInfo.liked--;
        }
    }

    render() {
        if (!this.props.forumMessages) return null

        const { forumMessages } = this.props;
        console.log(forumMessages);
        const date = new Date(forumMessages.datePosted);

        return (
            <div className="forumGetPostsList">
                <div key={`${forumMessages.id}_message`} className="forumGetMessage">
                    <div>
                        <div className="forumGetMessageHeader">
                            <img className="forumGetProfileImage" src={forumMessages.profileImage}/>
                            <div>{forumMessages.userName}</div>
                        </div>
                        <div className="forumGetMessageBody">
                            <div dangerouslySetInnerHTML={{__html: forumMessages.message}} />
                            <div>
                                <img src={this.state.liked ? LikeFullImage : LikeImage} width="15px" height="15px" onClick={() => this.onLikedClicked()}/> {forumMessages.likes}
                            </div>
                        </div>
                    </div>

                    <div>
                        <span>{date.toLocaleString("fr",{day: 'numeric', month: 'long', year: 'numeric'})}</span>
                    </div>
                </div>
            </div>
        );
    }
}

export default connect(null, { getForum, getPostsFromForum })(ForumGet);
