import React from 'react';
import { connect } from 'react-redux';

import { menuChange, getForums } from '../../../actions';

class Forums extends React.Component {
    componentDidMount() {
        this.props.menuChange('forums');
        this.props.getForums();
    }

    renderForumList() {
        if (!this.props.forums) return null;

        return this.props.forums.map((forum) => {
            return (
                <div key={`forum_${forum.id}`}>
                    <div></div>
                    <div>
                        <div>
                            <h3>{forum.titleForum}</h3>
                            <p>{forum.tags}</p>
                        </div>
                    </div>
                </div>
            );
        });
    }

    render() {
        return (
            <div className="Forums">
                {this.renderForumList()}
            </div>
        );
    }
};

const mapStateToProps = (state) => {
    return {  forums: state.forum.forums  };
}

export default connect(mapStateToProps, {menuChange, getForums})(Forums);
