import React from 'react';
import { connect } from 'react-redux';

import { menuChange, getForums } from '../../../actions';

import './Forum.scss';

class Forums extends React.Component {
    componentDidMount() {
        this.props.menuChange('forums');
        this.props.getForums();
    }

    renderForumList() {
        if (!this.props.forums) return null;

        return this.props.forums.map((forum) => {
            return (
                <div key={`forum_${forum.id}`} className="forumElement">
                    <div>
                        {forum.numberOfLikes}
                    </div>
                    <div>
                        <div>
                            <h3>{forum.titleForum}</h3>
                            <p>{forum.tags}</p>
                        </div>
                        <div>
                            <div>
                                <img />
                                <div>Posted by <strong>{forum.postedBy}</strong></div>
                                <div>{forum.dateCreated} ago</div>
                            </div>
                            <div>
                                <div>{forum.numberOfViews} views</div>
                                <div>{forum.numberOfMessages} messages</div>
                            </div>
                        </div>
                    </div>
                </div>
            );
        });
    }

    render() {
        return (
            <div className="Forums">
                {this.renderForumList()}
            </div>
        );
    }
};

const mapStateToProps = (state) => {
    return {  forums: state.forum.forums  };
}

export default connect(mapStateToProps, {menuChange, getForums})(Forums);
