import React from 'react';
import { connect } from 'react-redux';
import { Route } from 'react-router-dom';

import Logo from '../../Header/Logo';
import GoogleAuth from '../../GoogleAuth';
import JoinIn from './JoinIn';
import Search from './Search';
import { menuChange } from '../../../actions';

import './Home.scss';

class Home extends React.Component {
    state = { showJoinIn: true };

    constructor(props) {
        super(props);
        this.myRef = React.createRef();
    }

    componentDidMount() {
        this.props.menuChange('home');
    }

    onDismiss = (bool) => {
        this.setState({ showJoinIn: bool })
    }

    displayJoinIn = () => {
        if (!this.props.isSignedIn && this.state.showJoinIn) return <JoinIn onDismiss={this.onDismiss} />;
        return <div></div>;
    }

    renderIfNotSignedIn = () => {
        return (
            <div className="join">
                <GoogleAuth text="Sign In" buttonColor="red" displayButton />
                <GoogleAuth text="Log In" buttonColor="blue" displayButton />
            </div>
        );
    }
    renderIfSignedIn() {
        return (
            <div>
                <div className="join">
                    <img className="profileImage" src={this.props.userInfo.profileImage} width="35px" height="35px"/>
                </div>
                <div>
                    <Route path="/home/pinned" component={() => {
                        <React.Fragment>
                            <div className="home_menu">
                                <div className="selected"><Link to="/home/pinned">Cours pinnés</Link></div>
                                <div><Link to="/home/uploaded">Cours que j'ai fait</Link></div>
                            </div>
                            <div ref={this.myRef}>
                                <Search notActive />
                            </div>
                        </React.Fragment>
                    }} />

                    <Route path="/home/pinned" component={() => {
                        <React.Fragment>
                            <div className="home_menu">
                                <div><Link to="/home/pinned">Cours pinnés</Link></div>
                                <div className="selected"><Link to="/home/uploaded">Cours que j'ai fait</Link></div>
                            </div>
                            <div ref={this.myRef}>
                                <Search notActive IncludeUserId/>
                            </div>
                        </React.Fragment>
                    }} />
                </div>
            </div>
        );
    }

    render() {
        return (
            <div className="home">
                <GoogleAuth /> 
                {this.props.isSignedIn !== true ? this.renderIfNotSignedIn() : this.renderIfSignedIn()}
                {this.displayJoinIn()}
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {  isSignedIn : state.auth.isSignedIn, userInfo: state.auth.userInfo };
}

export default connect(mapStateToProps, {menuChange})(Home);
