import React from 'react';

import RightArrow from '../../rightArrow.png';

import './Dropdown.scss';

class Dropdown extends React.Component {
    state = {dropdownClick: false, elementChosen: null}

    componentDidMount() {
        this.setState({elementChosen: this.props.children[0]});
    }

    render() {
        console.log(this.state.elementChosen.key);
        return (
            <div>
                <div className="dropdown">
                    <span>{this.state.elementChosen}</span>
                    <img onClick={()=>this.setState({dropdownClick: !this.state.dropdownClick})} className={this.state.dropdownClick ? "clicked" : ""} src={RightArrow} width="10px" height="10px" />
                </div>
                <div>
                    {this.state.dropdownClick ? this.props.children.toArray.filter((elem) => elem === this.state.elementChosen) : ""}
                </div>
            </div>
        );
    }
}

export default Dropdown;
