import React from 'react';
import { connect } from 'react-redux';
import {getForum} from '../../../actions';


class ForumGet extends React.Component {
    state = {liked: null}

    componentDidMount() {
        this.props.getForum(this.props.match.params.id);
    }

    onLikedClicked = (bool) => {
        if (bool) {
            if (this.state.liked === null) {
                this.setState({liked:true});
                this.props.likedContent(this.props.courseInfo.id, 1, this.props.courseInfo.creator);
                this.props.courseInfo.liked++;
            } else if (this.state.liked === false) {
                this.setState({liked:true});
                this.props.likedContent(this.props.courseInfo.id, 2, this.props.courseInfo.creator);
                this.props.courseInfo.liked+=2;
            } else {
                this.setState({liked:null});
                this.props.likedContent(this.props.courseInfo.id, -1, this.props.courseInfo.creator);
                this.props.courseInfo.liked--;
            }

        } else {
            if (this.state.liked === null) {
                this.setState({liked:false});
                this.props.likedContent(this.props.courseInfo.id, -1, this.props.courseInfo.creator);
                this.props.courseInfo.liked--;
            } else if (this.state.liked === true) {
                this.setState({liked:false});
                this.props.likedContent(this.props.courseInfo.id, -2, this.props.courseInfo.creator);
                this.props.courseInfo.liked-=2;
            } else {
                this.setState({liked:null});
                this.props.likedContent(this.props.courseInfo.id, 1, this.props.courseInfo.creator);
                this.props.courseInfo.liked++;
            }
        }
    }

    render() {
        if (!this.props.forumInfo) return null 

        return (
            <div>Forum get {this.props.forumInfo}</div>
        );
    }
}

const mapStateToProps = (state, ownProps) => {
    return {  forumInfo: state.forum[`forum_${ownProps.match.params.id}`] };
};

export default connect(mapStateToProps, {getForum})(ForumGet);
